package javacore;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String name = "Krutarth";
		
		switch (name) {
		case "Krutarth":
			System.out.println("my name");
			
			break;
			
		case "ketan":
		System.out.println("dad");
		break;
		
		case "rahul":
			System.out.println("brother");
			break;

		default:
			System.out.println("NO mathc");
			break;
		}
	}

}
